mapper.select:
select id, pw, name,birth,gender,phone,email, free,cdate ,dan
from member 
where id = #{id}

mapper.update:
		update member 
    	   set pw = #{pw},
    	   	   phone = #{phone}, 
        	   email = #{email},
        	   free = #{free}
    	 where id = #{id}

mapper.select:
select count(id)
from member
where id = #{id}

select id 
from member 
where name = #{name} and email = #{email}

select * 
from member 
where id = #{id} and email = #{email}

mapper.update:
update member 
set dancntnumber = dancntnumber + 1 
where id = #{id}

mapper.select:
select dancntnumber 
from member 
where id = #{id}


