mapper.select:
select id, pw, name,birth,gender,phone,email, free,cdate 
from member 
where id = #{id}

mapper.update:
		update member 
    	   set pw = #{pw},
    	   	   phone = #{phone}, 
        	   email = #{email},
        	   free = #{free}
    	 where id = #{id}
    	   and pw = #{pw}
